by_url_and_queue_time.map:
function(doc) {
  var status = {
    aborted: doc['aborted'],
    completed: doc['completed'],
    depth: doc['depth']
  };

  emit([doc['url'], doc['queued_at']], [1, doc['queued_at'], status]);
}

by_url_and_queue_time.reduce:
function (keys, values, rereduce) {
  var i, status, maxtime = 0;

  for(i = 0; i < values.length; ++i) {
    if (maxtime < values[i][1]) {
      maxtime = values[i][1];
      status = values[i][2];
    }
  }

  return [values.length, maxtime, status];
}
